import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function Grid() {
  const { id } = useParams(); // Получение значения id из параметров пути

  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Выполнение запроса для получения участников турнира
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/participants`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при загрузке участников');
        }
        return response.json();
      })
      .then(data => {
        setParticipants(data);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  return (
    <div>
      <h1>Турнирная сетка</h1>
      <div className="participants">
        <h2>Участники</h2>
        <ul>
          {participants.map(participant => (
            <li key={participant.id}>{participant.name}</li>
          ))}
        </ul>
      </div>
      <div className="bracket">
        {/* Код для отображения турнирной сетки */}
      </div>
    </div>
  );
}

export default Grid;
