import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import '../css/grid.css';

function Grid() {
  const { id } = useParams(); // Получение значения id из параметров пути
  const [participants, setParticipants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [pairs, setPairs] = useState([]);


  useEffect(() => {
    // Выполнение запроса для получения участников турнира
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при загрузке участников');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data.participants)) {
          console.error('Участники должны быть представлены в виде массива');
          return;
        }
        setParticipants(data.participants);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  return (
    <div className='grid'>
        <Header />
            <div className='tour_grid'>
                <div className='back'>
                    <h3>Турнирная сетка</h3>
                    <Link to={`/tournament/${id}/`}>Назад</Link>
                </div>
                <div className="participants">
                    <ul>
                    {participants.map(participant => (
                        <li key={participant.user.id}>{participant.user.username}</li>
                    ))}
                    </ul>
                </div>
                <div className="bracket">
                    {/* Код для отображения турнирной сетки */}
                </div>
            </div>
    </div>
  );
}

export default Grid;
