import React, { Component } from 'react';
import { Link } from "react-router-dom";
import '../css/tournaments.css';

class Tournaments extends Component {
  state = {
    tournaments: [],
  };

  componentDidMount() {
    fetch('https://carxtours.com/api/four_latest/')
      .then(res => res.json())
      .then(data => this.setState({ tournament: data }))
      .catch(err => console.log(err));
  }  

  render() {
    const { tournaments } = this.state;

    return (
      <div className='tournaments'>
        {tournaments.map((tournament) => (
          <div className='block' key={tournament.id}>
            <div className='left'>
              <p className='date'>{tournament.date}</p>
              <p className='organization'>{tournament.organization}</p>
            </div>

            <div className='right'>
              <p className='tourName'>{tournament.name}</p>
              <p className='map'>{tournament.map}</p>
            </div>
          </div>
        ))}
        
        <div className='tournaments-button'>
          <Link to='/ivents'>Показать больше турниров</Link>
        </div>
      </div>
    );
  }
}

export default Tournaments;
