import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import '../css/grid.css';

function Grid() {
  const { id } = useParams(); // Получение значения id из параметров пути
  const [pairs, setPairs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [winners, setWinners] = useState([]);
  const [tournamentWinner, setTournamentWinner] = useState(null);

  useEffect(() => {
    // Выполнение запроса для получения участников турнира
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при загрузке участников');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data.participants)) {
          console.error('Участники должны быть представлены в виде массива');
          return;
        }
        const pairsData = [];
        for (let i = 0; i < data.participants.length; i += 2) {
          const participant1 = data.participants[i];
          const participant2 = data.participants[i + 1];
          const pairData = {
            participant1: null,
            participant2: null,
            winner: null,
          };
          if (participant1 && participant1.user) {
            pairData.participant1 = {
              id: participant1.user.id,
              username: participant1.user.username,
              try1: participant1.try1,
              try2: participant1.try2,
            };
          }
          if (participant2 && participant2.user) {
            pairData.participant2 = {
              id: participant2.user.id,
              username: participant2.user.username,
              try1: participant2.try1,
              try2: participant2.try2,
            };
          }
          pairsData.push(pairData);
        }
        setPairs(pairsData);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  const handleWinnerSelect = (pairIndex, winnerIndex) => {
    const updatedPairs = pairs.map((pair, index) => {
      if (index === pairIndex) {
        return {
          ...pair,
          winner: winnerIndex,
        };
      }
      return pair;
    });
    setPairs(updatedPairs);
  };

  useEffect(() => {
    // Обновление списка победителей
    const updatedWinners = pairs.map(pair => pair.winner);
    setWinners(updatedWinners);
  }, [pairs]);

  useEffect(() => {
    // Определение победителя турнира
    if (winners.length === 1) {
      setTournamentWinner(winners[0]);
    } else {
      setTournamentWinner(null);
    }
  }, [winners]);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  return (
    <div className="grid">
      <Header />
      <div className="tour_grid">
        <div className="back">
          <h3>Турнирная сетка</h3>
          <Link to={`/tournament/${id}/`}>Назад</Link>
        </div>
        <div className="participants">
          <ul>
            {pairs.map((pair, index) => (
              <li key={index}>
                <div>
                  <button
                    disabled={pair.winner !== null}
                    onClick={() => handleWinnerSelect(index, 0)}
                  >
                    {pair.participant1 ? pair.participant1.username : 'Участник не определен'}
                  </button>
                </div>
                <div>
                  <button
                    disabled={pair.winner !== null}
                    onClick={() => handleWinnerSelect(index, 1)}
                  >
                    {pair.participant2 ? pair.participant2.username : 'Участник не определен'}
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
        {tournamentWinner ? (
          <div className="winner">
            <h4>Победитель турнира: {tournamentWinner}</h4>
          </div>
        ) : null}
      </div>
    </div>
  );
}

export default Grid;
