import React, { Component } from 'react';
import '../css/news.css';
import { Link } from 'react-router-dom';

class News extends Component {
  state = {
    tournaments: []
  }

  componentDidMount() {
    fetch('https://carxtours.com/api/tournaments/')
      .then(res => res.json())
      .then(data => this.setState({ tournaments: data.reverse() }))
      .catch(err => console.log(err));
  }

  render() {
    const { tournaments } = this.state;
    return (
      <div className='news'>
        {tournaments.map(tournament => (
          <div key={tournament.id}>
            <div className='head'>
              <p>Организатор</p>
              <p>/</p>
              <p>Название турнира</p>
              <p>/</p>
              <p>Карта</p>
              <p>/</p>
              <p>Дата</p>
            </div>
            <div className='text'>
              <p>{tournament.short_org_name}</p>
              <p>{tournament.tournament_name}</p>
              <p>{tournament.map}</p>
              <p>{tournament.date}</p>
            </div>
            <div className='link'>
              <Link to={`/tournament/${tournament.id}`}>Перейти</Link>
            </div>
          </div>
        ))}
      </div>
    )
  }
};

export default News;
