import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import '../css/grid.css';

function Grid() {
  const { id } = useParams(); // Получение значения id из параметров пути
  const [pairs, setPairs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Выполнение запроса для получения участников турнира
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при загрузке участников');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data.participants)) {
          console.error('Участники должны быть представлены в виде массива');
          return;
        }
        const pairsData = [];
        for (let i = 0; i < data.participants.length; i += 2) {
          const participant1 = data.participants[i];
          const participant2 = data.participants[i + 1];
          pairsData.push({
            participant1: {
              id: participant1.user.id,
              username: participant1.user.username,
              try1: participant1.try1,
              try2: participant1.try2,
            },
            participant2: {
              id: participant2.user.id,
              username: participant2.user.username,
              try1: participant2.try1,
              try2: participant2.try2,
            },
          });
        }
        setPairs(pairsData);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  return (
    <div className="grid">
      <Header />
      <div className="tour_grid">
        <div className="back">
          <h3>Турнирная сетка</h3>
          <Link to={`/tournament/${id}/`}>Назад</Link>
        </div>
        <div className="participants">
          <ul>
            {pairs.map((pair, index) => (
              <li key={index}>
                <div>{pair.participant1.username}</div>
                <div>{pair.participant2.username}</div>
              </li>
            ))}
          </ul>
        </div>
        <div className="bracket">
          {/* Код для отображения турнирной сетки */}
        </div>
      </div>
    </div>
  );
}

export default Grid;
