import React, { useEffect, useState } from 'react';

function Grid() {
  const [participants, setParticipants] = useState([]);

  useEffect(() => {
    // Получение участников турнира и обновление состояния
    fetchParticipants();
  }, []);

  const fetchParticipants = () => {
    // Выполнение API-запроса для получения участников турнира
    // Замените конечную точку API и добавьте необходимые заголовки
    fetch('https://carxtours.com/api/tournaments/participants')
      .then(response => response.json())
      .then(data => {
        setParticipants(data);
      })
      .catch(error => {
        console.error('Ошибка при получении участников:', error);
      });
  };

  // Отрисовка участников в турнирной сетке
  const renderParticipants = () => {
    return participants.map(participant => (
      <div key={participant.id}>
        {/* Добавьте необходимый JSX-код для отображения участника в турнирной сетке */}
        <p>{participant.name}</p>
      </div>
    ));
  };

  return (
    <div>
      <h1>Турнирная сетка</h1>
      {/* Отрисовка участников в турнирной сетке */}
      {renderParticipants()}
    </div>
  );
}

export default Grid;
