import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import '../css/grid.css';

function Grid() {
  const { id } = useParams();
  const [rounds, setRounds] = useState([]);
  const [tournamentWinner, setTournamentWinner] = useState(null);
  const [ownerId, setOwnerId] = useState(null);  // обновите эту строку
  const [winners, setWinners] = useState([]);
  const [losers, setLosers] = useState([]);
  const [thirdPlaceFighters, setThirdPlaceFighters] = useState([]);
  const [thirdPlaceWinner, setThirdPlaceWinner] = useState(null);
  const [thirdPlaceMatch, setThirdPlaceMatch] = useState(null);

  useEffect(() => {
    const activeRound = rounds[rounds.length - 1];
    if (!activeRound) return;

    const allPairsHaveWinners = activeRound.every(pair => pair.winner !== null);
    if (!allPairsHaveWinners) return;

    const winners = activeRound.map(pair => pair.winner);
    const losers = activeRound.map(pair => pair.players.find(player => player !== pair.winner));

    if (winners.length === 2) {
      setThirdPlaceFighters(losers); // Это раунд за 3 место
    } else if (winners.length === 1) {
      setTournamentWinner(winners[0]);
      setWinners(prevWinners => [...prevWinners, winners[0]]); // Добавляем победителя в список победителей
      return;
    }

    const newRound = createRound(winners);
    setRounds(prevRounds => [...prevRounds, newRound]);

  }, [rounds]);

  const handleWinnerSelect = (roundIndex, pairIndex, winnerIndex) => {
    const currentUserId = parseInt(window.localStorage.getItem('userId')); // Получаем текущий userId из localStorage и преобразуем его в число
    
    // Проверьте, является ли текущий пользователь владельцем, прежде чем разрешить выбор победителя
    if (currentUserId !== ownerId) return; // Если текущий пользователь не является владельцем, мы прекращаем выполнение функции    
    const updatedRounds = rounds.map((round, index) => {
      if (index === roundIndex) {
        const updatedPairs = round.map((pair, pIndex) => {
          if (pIndex === pairIndex) {
            const winner = pair.players[winnerIndex] ? pair.players[winnerIndex] : null;
            console.log('Selected winner:', winner);  // Добавьте это
            return {
              ...pair,
              winner: winner ? { ...winner } : null,
            };
          }
          return pair;
        });
        return updatedPairs;
      }
      return round;
    });
    if (winners.length === 2) {
      setThirdPlaceMatch({ players: losers, winner: null }); // Это раунд за 3 место
    }    

    setRounds(updatedRounds);
  };

  const handleThirdPlaceWinnerSelect = (winnerIndex) => {
    const currentUserId = parseInt(window.localStorage.getItem('userId')); // Получаем текущий userId из localStorage и преобразуем его в число
  
    if (currentUserId !== ownerId) return;
  
    if (thirdPlaceMatch && thirdPlaceMatch.players[winnerIndex]) {
      setThirdPlaceMatch({
        ...thirdPlaceMatch,
        winner: thirdPlaceMatch.players[winnerIndex],
      });
      setThirdPlaceWinner(thirdPlaceMatch.players[winnerIndex]);
    }
  };
  

  const thirdPlaceWinnerName = thirdPlaceWinner ? thirdPlaceWinner.username : '';

  useEffect(() => {
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Error loading participants');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data.participants)) {
          console.error('Participants must be an array');
          return;
        }
        const participants = data.participants.map(participant => ({
          id: participant.user.id,
          username: participant.user.username,
          try1: participant.try1,
          try2: participant.try2,
        }));
        setRounds([createRound(participants)]);
        setOwnerId(data.owner); // Обновите состояние isOwner на основе ответа API
        console.log('Owner Id:', data.owner); // Добавьте это
      })
      .catch(err => console.error(err));
  }, [id]);
  
  return (
    <div className="grid">
      <Header />
      <div className="tour_grid">
        <div className="back">
          <h3>Турнирная сетка</h3>
          <Link to={`/tournament/${id}/`}>Назад</Link>
        </div>
        {rounds.map((round, roundIndex) => (
          <div className="round" key={roundIndex}>
            <h4>Round {roundIndex + 1}</h4>
            <ul>
              {round.map((pair, pairIndex) => (
                <li key={pairIndex}>
                  <div className='fp'>
                    <button
                      disabled={pair.winner !== null}
                      onClick={() => handleWinnerSelect(roundIndex, pairIndex, 0)}
                    >
                      {pair.players[0] ? pair.players[0].username : 'Участник не определен'}
                    </button>
                  </div>
                  <div className='tp'>
                    <button
                      disabled={pair.winner !== null || !pair.players[1]}
                      onClick={() => handleWinnerSelect(roundIndex, pairIndex, 1)}
                    >
                      {pair.players[1] ? pair.players[1].username : 'Участник не определен'}
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        ))}
        {tournamentWinner && (
          <div /*className="winner"*/ className='test'>
            <h4>Победитель турнира: {tournamentWinner.username}</h4>
          </div>
        )}
      </div>

      {thirdPlaceWinner && (
        <div /*className="thirdPlaceWinner"*/>
          <h4>Победитель за 3 место: {thirdPlaceWinnerName}</h4>
        </div>
      )}
      <div /*className="top10"*/>
        <h4>Топ-10 участников:</h4>
        {winners.slice(0, 10).map((winner, index) => (
          <div key={index}>{winner.username}</div>
        ))}
      </div>

      {thirdPlaceMatch && (
  <div /*className="round"*/>
    <h4>Матч за третье место</h4>
    <ul>
      <li>
        <div className='fp'>
          <button
            disabled={thirdPlaceMatch.winner !== null}
            onClick={() => handleThirdPlaceWinnerSelect(0)}
          >
            {thirdPlaceMatch.players[0] ? thirdPlaceMatch.players[0].username : 'Участник не определен'}
          </button>
        </div>
        <div className='tp'>
          <button
            disabled={thirdPlaceMatch.winner !== null || !thirdPlaceMatch.players[1]}
            onClick={() => handleThirdPlaceWinnerSelect(1)}
          >
            {thirdPlaceMatch.players[1] ? thirdPlaceMatch.players[1].username : 'Участник не определен'}
          </button>
        </div>
      </li>
    </ul>
  </div>
)}
    </div>
  );
}

export default Grid;

function createRound(participants) {
  const pairs = [];
  for (let i = 0; i < participants.length; i += 2) {
    const player1 = participants[i];
    const player2 = participants[i + 1] || null;
    const pair = {
      players: [player1, player2],
      winner: null,
    };
    pairs.push(pair);
  }
  return pairs;
}
