import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import '../css/grid.css';

function Grid() {
  const { id } = useParams();
  const [pairs, setPairs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [winners, setWinners] = useState([]);
  const [tournamentWinner, setTournamentWinner] = useState(null);

  useEffect(() => {
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Ошибка при загрузке участников');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data.participants)) {
          console.error('Участники должны быть представлены в виде массива');
          return;
        }
        const participants = data.participants.map(participant => ({
          id: participant.user.id,
          username: participant.user.username,
          try1: participant.try1,
          try2: participant.try2,
        }));
        setPairs(createPairs(participants));
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  useEffect(() => {
    const updatedWinners = pairs.map(pair => pair.winner);
    setWinners(updatedWinners);
  }, [pairs]);

  useEffect(() => {
    if (winners.length === 1) {
      setTournamentWinner(winners[0]);
    } else {
      setTournamentWinner(null);
      if (winners.length > 0) {
        const participants = winners.map((winner, index) => ({
          id: winner.id,
          username: winner.username,
          try1: winner.try1,
          try2: winner.try2,
        }));
        setPairs(createPairs(participants));
      }
    }
  }, [winners]);

const handleWinnerSelect = (pairIndex, winnerIndex) => {
  const updatedPairs = pairs.map((pair, index) => {
    if (index === pairIndex) {
      const winner = pair.players[winnerIndex] ? pair.players[winnerIndex] : null;
      return {
        ...pair,
        winner: winner ? { ...winner } : null,
      };
    }
    return pair;
  });
  setPairs(updatedPairs);
};


  return (
    <div className="grid">
      <Header />
      <div className="tour_grid">
        <div className="back">
          <h3>Турнирная сетка</h3>
          <Link to={`/tournament/${id}/`}>Назад</Link>
        </div>
        <div className="participants">
          <ul>
            {pairs.map((pair, index) => (
              <li key={index}>
                <div>
                  <button
                    disabled={pair.winner !== null}
                    onClick={() => handleWinnerSelect(index, 0)}
                  >
                    {pair.players[0] ? pair.players[0].username : 'Участник не определен'}
                  </button>
                </div>
                <div>
                  <button
                    disabled={pair.winner !== null}
                    onClick={() => handleWinnerSelect(index, 1)}
                  >
                    {pair.players[1] ? pair.players[1].username : 'Участник не определен'}
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>
        {tournamentWinner && (
          <div className="winner">
            <h4>Победитель турнира: {tournamentWinner.username}</h4>
          </div>
        )}
      </div>
    </div>
  );
}

export default Grid;

// Функция для создания пар
function createPairs(participants) {
  const pairs = [];
  for (let i = 0; i < participants.length; i += 2) {
    const player1 = participants[i];
    const player2 = participants[i + 1];
    const pair = {
      players: [player1, player2],
      winner: null,
    };
    pairs.push(pair);
  }
  return pairs;
}
