const TournamentBlock = ({ tournament }) => (
  <div className='block'>
    <div className='left'>
      <p className='date'>{tournament.date}</p>
      <p className='organization'>{tournament.organization}</p>
    </div>

    <div className='right'>
      <p className='tourName'>{tournament.name}</p>
      <p className='map'>{tournament.map}</p>
    </div>
  </div>
);

const Tournaments = () => {
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    fetch('https://carxtours.com/api/latest/')
      .then(res => res.json())
      .then(data => setTournaments(data))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className='tournaments'>
      {tournaments.map(tournament => (
        <TournamentBlock key={tournament.id} tournament={tournament} />
      ))}

      <div className='tournaments-button'>
        <Link to='/ivents'>Показать больше турниров</Link>
      </div>
    </div>
  );
};

export default Tournaments;
