import React, { Component } from 'react'
import Header from '../components/Header';
import { useParams, Link } from 'react-router-dom';
import '../css/grid.css';

export default class Grid extends Component {
  state = {
    tournament: null,
    error: null,
  };

  componentDidMount() {
    // Replace with the actual ID you want to use.
    const { id } = useParams();

    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Error retrieving tournament data.');
        }
      })
      .then((data) => {
        this.setState({ tournament: data });
      })
      .catch((error) => {
        this.setState({ error });
      });
  }

  render() {
    const { tournament, error } = this.state;

    if (error) {
      return <div>Error: {error.message}</div>;
    }

    if (!tournament) {
      return <div>Loading...</div>;
    }

    return (
      <div>
        <Header/>
        <h2>{tournament.tournament_name}</h2>
        <ul>
          {tournament.participants.map((participant) => (
            <li key={participant.user.id}>
              {participant.user.username}: {participant.try1}, {participant.try2}
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
