import React, { Component } from 'react';
import '../css/news.css';

class News extends Component {
  state = {
    tournament: {}
  }

  componentDidMount() {
    fetch('https://carxtours.com/api/latest/')
      .then(res => res.json())
      .then(data => this.setState({ tournament: data }))
      .catch(err => console.log(err));
  }

  render() {
    const { tournament } = this.state;
    return (
      <div className='news'>
          <div className='text'>
            <p>{tournament.short_org_name}</p>  // Замените на соответствующие поля
            <p>{tournament.tournament_name}</p>
            <p>{tournament.map}</p>
            <p>{tournament.date}</p>
          </div>
        <div className='image'>

        </div>
      </div>
    )
  }
};

export default News;
