import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header';
import '../css/create.css';

export default function Create() {
  const [shortOrgName, setShortOrgName] = useState('');  
  const [fullOrgName, setFullOrgName] = useState('');  
  const [tournamentName, setTournamentName] = useState('');  
  const [date, setDate] = useState('');  // новый стейт для даты
  const [map, setMap] = useState('');  // Добавьте состояние для каждого поля ввода и textarea
  const [mapLink, setMapLink] = useState('');
  const [task, setTask] = useState('');
  const [example, setExample] = useState('');
  const [reglament, setReglament] = useState('');
  const [visualReglament, setVisualReglament] = useState('');
  const [maxPilots, setMaxPilots] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://45.10.110.89/api/auth/userprofile/', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${localStorage.getItem('token')}`,
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Не авторизован');
        }
        return response.json();
      })
      .then(data => {
        setShortOrgName(data.userprofile.short_org_name);
        setFullOrgName(data.userprofile.full_org_name);
      })
      .catch(err => {
        console.error(err);
        navigate('/login'); // или любой другой путь к вашей странице авторизации
      });
  }, []);

  const handleTournamentNameChange = (e) => {
    setTournamentName(e.target.value);
  };

  const handleDateChange = (e) => {   // новая функция для обработки изменения даты
    setDate(e.target.value);
  };

  const handleMapChange = (e) => {   // обработчик изменений для карты
    setMap(e.target.value);
  };

  const handleMapLinkChange = (e) => {
    setMapLink(e.target.value);
  };

  const handleTaskChange = (e) => {
    setTask(e.target.value);
  };

  const handleExampleChange = (e) => {
    setExample(e.target.value);
  };

  const handleReglamentChange = (e) => {
    setReglament(e.target.value);
  };

  const handleVisualReglamentChange = (e) => {
    setVisualReglament(e.target.value);
  };

  const handleMaxPilotsChange = (e) => {
    const value = e.target.value;
    setMaxPilots(value === '' ? null : value);
  };

  const handleTournamentCreate = () => {
    const data = {
      owner: localStorage.getItem('userId'),  // здесь должен быть идентификатор пользователя
      short_org_name: shortOrgName,
      full_org_name: fullOrgName,
      tournament_name: tournamentName,
      date: date,
      map: map,
      map_link: mapLink,
      task: task,
      example: example,
      reglament: reglament,
      visual_reglament: visualReglament,
      max_pilots: maxPilots === '' ? null : maxPilots,
    };
  
    fetch('http://45.10.110.89/api/tournaments/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${localStorage.getItem('token')}`,
      },
      body: JSON.stringify(data),
    })
    .then(response => {
      if (!response.ok) {
        // Попытайтесь сначала получить детали ответа как текст.
        return response.text().then(text => {
          // Выводите эти детали в консоль.
          console.error(text);
          // Теперь вы можете выбросить своё исключение.
          throw new Error('Ошибка при создании турнира');
        });
      }
      return response.json();
    })
    .then(data => {
      navigate(`/tournament/${data.id}`);
    })
    .catch(err => console.error(err));
    };

  return (
    <div>
      <Header/>
      <div className='create'>
        <p>Сокращённое название организации: {shortOrgName}</p>
        <p>Полное название организации: {fullOrgName}</p>
        <div className='tour_name'>
          <label>(Обязательно) Название турнира:</label>
          <input value={tournamentName} onChange={handleTournamentNameChange} />
        </div>
        <div className='tour_date'>
          <label>(Обязательно) Дата проведения:</label>
          <input type="date" value={date} onChange={handleDateChange} />
        </div>
        <div className='tour_map'>
          <label>(Обязательно) Карта:</label>
          <input value={map} onChange={handleMapChange}/>
        </div>
        <div className='tour_maplink'>
          <label>Ссылка на карту ( рекомендуется использовать Google Disk ):</label>
          <input value={mapLink} onChange={handleMapLinkChange} />
        </div>
        <div className='tour_example'>
          <label>(Обязательно) Примерный проезд ( ссылка на YouTube ):</label>
          <input value={example} onChange={handleExampleChange} />
        </div>
        <div className='tour_task'>
          <label>(Обязательно) Судейское задание:</label>
          <textarea value={task} onChange={handleTaskChange} />
        </div>
        <div className='tour_reglament'>
          <label>(Обязательно) Регламент:</label>
          <textarea value={reglament} onChange={handleReglamentChange} />
        </div>
        <div className='tour_visual_reglament'>
          <label>Визуальный регламент:</label>
          <textarea value={visualReglament} onChange={handleVisualReglamentChange} />
        </div>
        <div className='tour_pilots'>
          <label>Максимальное кол-во пилотов ( оставьте поле пустым если ограничение не нужно ):</label>
          <input value={maxPilots} onChange={handleMaxPilotsChange} />
        </div>
        <div className='tour_create'>
          <button onClick={handleTournamentCreate}>Создать турнир</button>
        </div>
      </div>
    </div>
  );
}
