import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import Header from '../components/Header';
import '../css/grid.css';

function Grid() {
  const { id } = useParams();
  const [rounds, setRounds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [tournamentWinner, setTournamentWinner] = useState(null);

  useEffect(() => {
    // проверяем, есть ли текущий активный раунд
    const activeRound = rounds[rounds.length - 1];
    if (!activeRound) return;

    // если в текущем раунде все пары имеют победителей
    const allPairsHaveWinners = activeRound.every(pair => pair.winner !== null);
    if (!allPairsHaveWinners) return;

    // создаем новый раунд с победителями текущего
    const winners = activeRound.map(pair => pair.winner);
    const newRound = createRound(winners);

    // добавляем новый раунд в массив раундов
    setRounds(prevRounds => [...prevRounds, newRound]);

    // если в новом раунде только одна пара и у этой пары только один игрок
    // то мы можем назначить этого игрока победителем турнира
    if (newRound.length === 1 && newRound[0].players[1] === null) {
      setTournamentWinner(newRound[0].players[0]);
    }
  }, [rounds]);


  useEffect(() => {
    fetch(`https://carxtours.com/api/tournaments/detail/${id}/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Error loading participants');
        }
        return response.json();
      })
      .then(data => {
        if (!Array.isArray(data.participants)) {
          console.error('Participants must be an array');
          return;
        }
        const participants = data.participants.map(participant => ({
          id: participant.user.id,
          username: participant.user.username,
          try1: participant.try1,
          try2: participant.try2,
        }));
        setRounds([createRound(participants)]);
        setLoading(false);
      })
      .catch(err => console.error(err));
  }, [id]);

  const handleWinnerSelect = (roundIndex, pairIndex, winnerIndex) => {
    const updatedRounds = rounds.map((round, index) => {
      if (index === roundIndex) {
        const updatedPairs = round.map((pair, pIndex) => {
          if (pIndex === pairIndex) {
            const winner = pair.players[winnerIndex] ? pair.players[winnerIndex] : null;
            return {
              ...pair,
              winner: winner ? { ...winner } : null,
            };
          }
          return pair;
        });
        return updatedPairs;
      }
      return round;
    });
    setRounds(updatedRounds);
  };

  const tournamentWinnerName = tournamentWinner ? tournamentWinner.username : '';
  
  return (
    <div className="grid">
      <Header />
      <div className="tour_grid">
        <div className="back">
          <h3>Турнирная сетка</h3>
          <Link to={`/tournament/${id}/`}>Назад</Link>
        </div>
        {rounds.map((round, roundIndex) => (
          <div className="round" key={roundIndex}>
            <h4>Round {roundIndex + 1}</h4>
            <ul>
              {round.map((pair, pairIndex) => (
                <li key={pairIndex}>
                  <div>
                    <button
                      disabled={pair.winner !== null}
                      onClick={() => handleWinnerSelect(roundIndex, pairIndex, 0)}
                    >
                      {pair.players[0] ? pair.players[0].username : 'Участник не определен'}
                    </button>
                  </div>
                  <div>
                    <button
                      disabled={pair.winner !== null || !pair.players[1]}
                      onClick={() => handleWinnerSelect(roundIndex, pairIndex, 1)}
                    >
                      {pair.players[1] ? pair.players[1].username : 'Участник не определен'}
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        ))}
        {tournamentWinner && (
          <div className="winner">
            <h4>Победитель турнира: {tournamentWinner.username}</h4>
          </div>
        )}
      </div>
    </div>
  );
}

export default Grid;

function createRound(participants) {
  const pairs = [];
  for (let i = 0; i < participants.length; i += 2) {
    const player1 = participants[i];
    const player2 = participants[i + 1] || null;
    const pair = {
      players: [player1, player2],
      winner: null,
    };
    pairs.push(pair);
  }
  return pairs;
}
