import React, { Component } from 'react';
import '../css/news.css';

class News extends Component {
  state = {
    tournament: {}
  }

  componentDidMount() {
    fetch('https://carxtours.com/api/tournaments/latest/')
      .then(res => res.json())
      .then(data => this.setState({ tournament: data }))
      .catch(err => console.log(err));
  }

  render() {
    const { tournament } = this.state;
    return (
      <div className='news'>
          <div className='text'>
            <p>Самый новый турнир!</p>
          </div>
          <div className='text'>
            <p>Организатор</p>
            <p>/</p>
            <p>Название турнира</p>
            <p>/</p>
            <p>Карта</p>
            <p>/</p>
            <p>Дата</p>
          </div>
          <div className='text'>
            <p>{tournament.short_org_name}</p>
            <p>{tournament.tournament_name}</p>
            <p>{tournament.map}</p>
            <p>{tournament.date}</p>
          </div>
        <div className='image'>

        </div>
      </div>
    )
  }
};

export default News;
